#include <iostream>
#include "SecretDecoderTree.h"

using namespace std;


int main(int argc, char * argv[])
{

   CSecretDecoderTree s;
   s.BuildTree("fjkghibalsmrnopqz .tuvwxy!?cde", "065R023L025L165R");
//   s.BuildTree("fjkghibalsmrnopqz .tuvwxy!?cde", "065R025L165R");
   s.InorderPrintLeafLetters();

   cout << "Code for a " << s.GetCode("a").data() << endl << endl;
   cout << "Code for q " << s.GetCode("q").data() << endl << endl;

   cout << s.Decode(s.Encode("this is a test...").data()).data() << endl <<endl;
   cout << s.Encode("Testing program now").data() << endl <<endl;

   cout << s.Decode("0101111110000100001010111000111011010101100111000011110111001").data() << endl << endl;
   cout << s.Decode(s.Encode("abcdefghijklmnopqrstuvwxyz .!?").data()).data() << endl << endl;
   cout << s.Decode("").data() <<endl;

	return 0;
}

